import React from 'react';

export default class ApiCall extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: []
    };


  }

  componentDidMount() {
    fetch("http://localhost:8080/erick")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result.items
          });
        },
    
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }
  render() {
        function myFunction() {
        return 5;  // The function returns the product of p1 and p2
        }
    
        return(
            <div>
               
               <ul>
          {this.state.items.map(item => (
            <li key={item.name}>
              {item.name} {item.price}
            </li>
          ))}
        </ul>
            </div>
        );
    }
    
  }
